mapper.select:
		SELECT A.RNUM, A.YYMM, A.VER, 
		       A.TEAM_CD, 
		       (SELECT Z.COMN_CD_NM FROM TB_COMNCD Z WHERE Z.DIV_CD = 'CC01' AND Z.COMN_CD = A.TEAM_CD) TEAM_NM, 
		       A.GRP_NM, A.SYSTEM_NM, A.B2C_YN, 
               A.PRIORITY, 
               (SELECT Z.COMN_CD_NM FROM TB_COMNCD Z WHERE Z.DIV_CD = 'PC01' AND Z.COMN_CD = A.PRIORITY) PRIORITY_NM, 
               A.PACKAGE_NM, A.LINE_NO, A.RULE_NM
          FROM (
                SELECT ROWNUM RNUM, YYMM, VER, TEAM_CD, GRP_NM, SYSTEM_NM, B2C_YN, 
                       PRIORITY, PACKAGE_NM, LINE_NO, RULE_NM
                  FROM (
                        SELECT YYMM, VER, TEAM_CD, GRP_NM, SYSTEM_NM, B2C_YN, 
                               PRIORITY, PACKAGE_NM, LINE_NO, RULE_NM
                          FROM TB_BASEDATA
                         WHERE 1 = 1
                           AND YYMM = #{yymm}
				           AND VER = #{ver}

				           AND TEAM_CD = #{teamCd}


				           AND GRP_NM LIKE '%' || #{grpNm} || '%'


				           AND SYSTEM_NM LIKE '%' || #{systemNm} || '%'


				           AND B2C_YN = #{b2cYn}


				           AND PRIORITY = #{priority}


				           AND RULE_NM LIKE '%' || #{ruleNm} || '%'

                         ORDER BY YYMM, VER, TEAM_CD, GRP_NM, SYSTEM_NM, B2C_YN, 
                                  PRIORITY, PACKAGE_NM, LINE_NO, RULE_NM
                       )
                 WHERE 1 = 1
                   AND ROWNUM  <=  #{endNum}
               ) A
         WHERE A.RNUM  >=  #{startNum}

SELECT COUNT(*) PMD_CNT
  FROM TB_BASEDATA
 WHERE 1 = 1
   AND YYMM = #{yymm}
   AND VER = #{ver}

   AND TEAM_CD = #{teamCd}


   AND GRP_NM LIKE '%' || #{grpNm} || '%'


   AND SYSTEM_NM LIKE '%' || #{systemNm} || '%'


   AND B2C_YN = #{b2cYn}


   AND PRIORITY = #{priority}


   AND RULE_NM LIKE '%' || #{ruleNm} || '%'

SELECT TEAM_CD, (SELECT Z.COMN_CD_NM FROM TB_COMNCD Z WHERE Z.DIV_CD = 'CC01' AND Z.COMN_CD = A.TEAM_CD) TEAM_NM, SUM(CASE WHEN PRIORITY = 'PC101' THEN PMD_CNT ELSE 0 END) BLOCKER_COUNT, SUM(CASE WHEN PRIORITY = 'PC102' THEN PMD_CNT ELSE 0 END) CRITICAL_COUNT, SUM(CASE WHEN PRIORITY = 'PC103' THEN PMD_CNT ELSE 0 END) URGENT_COUNT, SUM(PMD_CNT) TOTAL_COUNT FROM TB_CHARTDATA A WHERE 0=0 AND YYMM = #{yymm} AND VER = #{ver}
AND TEAM_CD = #{teamCd}
GROUP BY TEAM_CD ORDER BY SUM(PMD_CNT) DESC, TEAM_CD

SELECT TEAM_CD, (SELECT Z.COMN_CD_NM FROM TB_COMNCD Z WHERE Z.DIV_CD = 'CC01' AND Z.COMN_CD = A.TEAM_CD) TEAM_NM, SYSTEM_NM, SUM(CASE WHEN PRIORITY = 'PC101' THEN PMD_CNT ELSE 0 END) BLOCKER_COUNT, SUM(CASE WHEN PRIORITY = 'PC102' THEN PMD_CNT ELSE 0 END) CRITICAL_COUNT, SUM(CASE WHEN PRIORITY = 'PC103' THEN PMD_CNT ELSE 0 END) URGENT_COUNT, SUM(PMD_CNT) TOTAL_COUNT FROM TB_CHARTDATA A WHERE YYMM = #{yymm} AND VER = #{ver}
AND TEAM_CD = #{teamCd}
AND SYSTEM_NM LIKE '%' || #{systemNm} || '%'
AND B2C_YN = #{b2cYn}
AND PRIORITY = #{priority}
AND RULE_NM LIKE '%' || #{ruleNm} || '%'
GROUP BY TEAM_CD, SYSTEM_NM ORDER BY TOTAL_COUNT DESC, TEAM_CD, SYSTEM_NM


SELECT /* (SELECT Z.COMN_CD_NM FROM TB_COMNCD Z WHERE Z.DIV_CD = 'CC01' AND Z.COMN_CD = A.TEAM_CD) TEAM_NM, */ 
       /* SYSTEM_NM, */
       RULE_NM,
       SUM(PMD_CNT) TOTAL_COUNT
  FROM TB_CHARTDATA A
 WHERE YYMM = #{yymm}
   AND VER = #{ver}

   AND TEAM_CD = #{teamCd}


   AND SYSTEM_NM LIKE '%' || #{systemNm} || '%'


   AND B2C_YN = #{b2cYn}


   AND PRIORITY = #{priority}


   AND RULE_NM LIKE '%' || #{ruleNm} || '%'

 GROUP BY RULE_NM --TEAM_CD, SYSTEM_NM, RULE_NM
 ORDER BY TOTAL_COUNT DESC, RULE_NM --TEAM_CD, SYSTEM_NM, RULE_NM


        SELECT RULE_NO, RULE_REF, RULE_LANG, RULE_CATEGORY, RULE_NM, PROD_YN, DEPRE_YN,
               SINCE_VER, PRIORITY, PRIORITY_NM, MESSAGE, DETAIL_DESC, BAD_CASE, GOOD_CASE
          FROM (
                SELECT ROWNUM RNUM, RULE_NO, RULE_REF, RULE_LANG, RULE_CATEGORY, RULE_NM, PROD_YN, DEPRE_YN,
                       SINCE_VER, PRIORITY, PRIORITY_NM, MESSAGE, DETAIL_DESC, BAD_CASE, GOOD_CASE
                  FROM (
                        SELECT RULE_NO, RULE_REF, RULE_LANG, RULE_CATEGORY, RULE_NM, PROD_YN, DEPRE_YN,
                               SINCE_VER, PRIORITY, PRIORITY || ' - ' || PRIORITY_NM PRIORITY_NM, 
                               MESSAGE, DETAIL_DESC, BAD_CASE, GOOD_CASE
                          FROM TB_PMDRULE
                         WHERE 0=0

                           AND RULE_LANG = #{ruleLang}


                           AND RULE_CATEGORY = #{ruleCategory}


                           AND PROD_YN = #{prodYn}


                           AND RULE_LANG = #{ruleLang}


				           AND PRIORITY = #{priority}


				           AND RULE_NM LIKE '%' || #{ruleNm} || '%'

                         ORDER BY RULE_NO
                       )
                 WHERE 0=0
                   AND ROWNUM  <=  #{endNum}
               )
         WHERE 0=0
           AND RNUM  >=  #{startNum}


SELECT COUNT(*)
  FROM TB_PMDRULE
 WHERE 0=0

   AND RULE_LANG = #{ruleLang}


   AND RULE_CATEGORY = #{ruleCategory}


   AND PROD_YN = #{prodYn}


   AND RULE_LANG = #{ruleLang}


   AND PRIORITY = #{priority}


   AND RULE_NM LIKE '%' || #{ruleNm} || '%'
